import { Header, Segment, Button, Icon, Item } from "semantic-ui-react"
import { useRouter } from "next/router"

function CartItemList({ products, user }) {
  const router = useRouter();

  function mapProductsToItem(products) {
    console.log(products)
   /*  return products.map( item => ({
      childkey: item.product._id,
      header: (
        <Item.Header as="a" onClick={() => {
          router.push(`/product?_id=${item.product._id}`)
        }}> 
          { item.product.name } 
        </Item.Header>
      ),
      image: item.product.mediaUrl,
      meta: `${item.product.quantity} x $${item.product.price}`,
      fluid: "true",
      extra: (
        <Button 
          basic
          icon="remove"
          floated="right"
          onClick={() => { console.log(item.product._id ) }}
        />
      )
    })) */
  }

  if (products.lenght === 0) {
    return (
      <Segment secondary color="teal" inverted textAlign="center" placeholder>
        <Header icon>
          <Icon name="shopping basket" />
          No products in your cart. Add some!
        </Header> 
        <div>
          {  user ? (
              <Button color="orange" content="View products" onClick={ () => {router.push("/")} } />
            ) : (
              <Button color="blue" content="Login and add products" onClick={() => { router.push("/login") }} />
            )
          }
        </div>
      </Segment>
    )
  } else {
    return (
      <Item.Group items={mapProductsToItem(products)} />
    )
  }

}

export default CartItemList
