import { Segment } from "semantic-ui-react"
import CartItemList from "../components/Cart/CartItemList"
import CartSummary from "../components/Cart/CartSummary"
import { parseCookies } from "nookies";
import baseUrl from "../utils/baseUrl";
import axios from "axios";
import { useState } from "react";

function Cart({ products, user }) {  
  const [cartProducts, setCartProducts] = useState(products)

  async function handleRemoveFromCart(productId) {
    console.log(productId)
  }

  return (
    <Segment>
      <CartItemList handleRemoveFunction={handleRemoveFromCart} user={ user } products={ products.cart }/>
      <CartSummary products={ products.cart } />
    </Segment>
  )
}

Cart.getInitialProps = async (ctx) => {
  const { reactreserve_token } = parseCookies(ctx);
  if (!reactreserve_token) {
    return {
      products: []
    }
  } else { 
    const url = `${baseUrl}/api/cart`;
    const payload = { headers: { Authorization: reactreserve_token }}
    const response = await axios.get(url, payload)
    return {
      products: response.data
    }
  }
}

export default Cart
