            } else {
              dispatch({ type: "CLEAR" })
            }
          })
          .catch(error => console.log(error))
      } catch(error) {
        cookie.remove("reactreserve_token");
        router.push("/login")
      }

    }
    if (token) {
      validateUser();
    } else {
      if (isProtectedRoute) {
        router.push("/login")
      }
    }
  })

  return (
    <Context.Provider value={[state, dispatch]}>
      { children }
    </Context.Provider>
  )
}

export default Authentication;
