import Product from "../../models/Product";
import connectDb from "../../utils/connectDb";
connectDb();
export default (async (req, res) => {
  try {
    const products = await Product.find();

    if (res.statusCode == 200) {
      res.json({
        success: true,
        products: products
      });
    } else {
      res.json({
        success: false
      });
    }
  } catch (error) {
    res.status(500).send("Server error in creating");
  }
});
